def calculate_edit_distance(str1, str2):
    
    #add an under dash at the beginning so it helps the code if there is insertion or deletion
    str1 = "-" + str1
    str2 = "-" + str2

    # the number of rows and columns needed for a matrix 
    # from the lenght of the strings to make sure all possible combinations are acounted for
    rows = len(str1)
    cols = len(str2)
    
    #2D matrix (distance_matrix) filled with zeros.
    # Each cell (i, j) in the matrix represents the edit distance between
    # the substrings str1[1:i+1] and str2[1:j+1].
    distance_matrix = [[0 for _ in range(cols)] for _ in range(rows)]

    # Initialize the first row and first column of the matrix.
    # These represent the edit distances for empty strings compared to the input strings.
    for j in range(cols):
        distance_matrix[0][j] = j

    for i in range(rows):
        distance_matrix[i][0] = i

    # Fill in the rest of the matrix 
    for i in range(1, rows):
        for j in range(1, cols):
             
            # Calculate the cost of matching or mismatching characters at the current positions.
             if str1[i] != str2[j]:
                 cost = 1
             else:
                 cost = 0

            # Calculate the edit distance by considering three possible operations:
            # 1. Insertion: distance_matrix[i][j - 1] + 1
            # 2. Deletion: distance_matrix[i - 1][j] + 1
            # 3. Substitution: distance_matrix[i - 1][j - 1] + cost
            # Choose the minimum of these three options.
             insertion = distance_matrix[i][j - 1] + 1
             deletion = distance_matrix[i - 1][j] + 1
             substitution = distance_matrix[i - 1][j - 1] + cost
             distance_matrix[i][j] = min(insertion, deletion, substitution)

    # The value at the bottom-right cell of the matrix represents the minimum edit distance.
    # Return this value as the result of the function.
    return distance_matrix[rows - 1][cols - 1]


if __name__ == "__main__":
    string1 = ''
    string2 = ''
    print(calculate_edit_distance(string1, string2))


